mapper.update:
update contest
title = #{title}, description = #{description}, start_time = #{startTime}, end_time = #{endTime}, visible = #{visible}, type = #{type}, password = #{password},
where id = #{id}

mapper.select:
SELECT c.id, c.title, c.start_time, c.end_time, c.duration, c.status, c.type, u.username AS createUserName, COUNT(cu.id) AS registerCount FROM contest c LEFT JOIN user u on c.create_user = u.id LEFT JOIN contest_user cu on c.id = cu.contest_id
AND title like concat('%', #{keyword}, '%')
AND status = #{status}
AND type = #{type}
GROUP BY c.id, c.title, c.start_time, c.end_time, c.duration, c.status, c.type, u.username ORDER BY c.start_time DESC
